import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './Card.scss';
import InputBox from '../InputBox/InputBox';
import DivButton from '../DivButton/DivButton';

export default class Card extends Component {
    constructor(props){
        super(props);
        this.state = {
            swapSampling_API: {
                method_used: "",
                solvent_quantity: "",
                solvent_name: "",
                default_recovery: ""
            },
            moc_display: false,
            divText: `Add MOC`
        }
    }
    // callback(){

    // }
    displayMiniCards(){

    }
    render() {
        return (
            <div className="card-container">
                <div className="card-content">
                    <InputBox 
                        label={"Method Used"}
                        isRequired={true}
                        type={"text"}
                        ref={"method_used"}
                        updateValue={(e) =>{
                            this.setState({
                                swapSampling_API: {
                                    method_used: ReactDOM.findDOMNode(this.refs.method_used.refs.input).value
                                }
                            },() => {
                                this.props.callback(this.state);
                            })
                        }}
                    />
                    <div>
                        <div>
                            <InputBox 
                                label={"Solvent name"}
                                isRequired={true}
                                type={"text"}
                                ref={"solvent_name"}
                                addInputStyle={`input-box-half`}
                                addClass={`input-box-container-half`}
                                updateValue={(e) =>{
                                    this.setState({
                                        swapSampling_API:{
                                            solvent_name: ReactDOM.findDOMNode(this.refs.solvent_name.refs.input).value
                                        } 
                                    },() => {
                                        this.props.callback(this.state);
                                    })
                                }}
                            />
                        </div>
                        <div>
                            <InputBox 
                                label={"Solvent Quantity"}
                                isRequired={true}
                                type={"text"}
                                ref={"solvent_quantity"}
                                addInputStyle={`input-box-half`}
                                addClass={`input-box-container-half`}
                                updateValue={(e) =>{
                                    this.setState({
                                        swapSampling_API:{
                                            solvent_quantity: ReactDOM.findDOMNode(this.refs.solvent_quantity.refs.input).value
                                        }
                                    },() => {
                                        this.props.callback(this.state);
                                    })
                                }}
                            />
                        </div>
                    </div>

                    <InputBox 
                        label={"Default Recovery"}
                        isRequired={true}
                        type={"text"}
                        ref={"default_recovery"}
                        updateValue={(e) =>{
                            this.setState({
                                swapSampling_API:{
                                    default_recovery: ReactDOM.findDOMNode(this.refs.default_recovery.refs.input).value
                                } 
                            },() => {
                                this.props.callback(this.state);
                            })
                        }}
                    />
                    <div>
                        <DivButton 
                            ref={"add_moc"}
                            text={this.state.divText}
                            updateValue={(e) =>{
                                this.setState({
                                    mob_display: !this.state.mob_display,

                                },() => {
                                    this.displayMiniCards();
                                })
                            }}
                        />
                    </div>
                </div>
            </div>
        )
    }
}
