import React, { Component } from 'react'
import ReactDOM from 'react-dom';
import InputBox from '../contents/InputBox/InputBox';

export default class CardContents extends Component {
    constructor(props){
        super(props);
        this.state = {
            target_residue_type_list: [],
            lod: "",
            loq: ""
        }
    }

