    render() {
        return (
            <div className="card-contents">
                <div className="card-content-items">
                    <div className="display-box">
                        <InputBox 
                            label={"LOD (in ppm)"}
                            isRequired={true}
                            type={"text"}
                            ref={"lod"}
                            addInputStyle={`input-box-half`}
                            addClass={`input-box-container-half`}
                            updateValue={(e) =>{
                                this.setState({
                                    lod: ReactDOM.findDOMNode(this.refs.lod.refs.input).value
                                },() => {
                                    this.props.setLQD_LODValues(this.state);
                                })
                            }}
                        />
                    </div>
                    <div className="display-box">
                        <InputBox 
                            label={"LOQ (in ppm)"}
                            isRequired={true}
                            type={"text"}
                            ref={"loq"}
                            addInputStyle={`input-box-half`}
                            addClass={`input-box-container-half`}
                            updateValue={(e) => {
                                this.setState({
                                    loq: ReactDOM.findDOMNode(this.refs.loq.refs.input).value
                                }, () => {
                                    this.props.setLQD_LODValues(this.state);
                                });
                            }}
                        />
                    </div>
                </div>
            </div>
        )
    }
}
