import React, { Component } from 'react'
import ReactDOM from 'react-dom';
import InputBox from '../contents/InputBox/InputBox';

export default class CardContents extends Component {
    constructor(props){
        super(props);
        this.state = {
            target_residue_type_list: [],
            lod: "",
            loq: ""
        }
    }
    updateLODValue(e){

        this.setState({
            lod: ReactDOM.findDOMNode(this.refs.lod.refs.input).value
        },() => {console.log("111", this.state)});

        this.props.setLQD_LODValues(this.state);
    }
    updateLOQValue(e){
        this.setState({
            loq: ReactDOM.findDOMNode(this.refs.loq.refs.input).value
        });
        this.props.setLQD_LODValues(this.state);
    }

    render() {
        return (
            <div className="card-contents">
                <div className="card-content-items">
                    <div className="display-box">
                        <InputBox 
                            label={"LOD (in ppm)"}
                            isRequired={true}
                            type={"text"}
                            ref={"lod"}
                            addInputStyle={`input-box-half`}
                            addClass={`input-box-container-half`}
                            updateValue={this.updateLODValue.bind(this)}
                        />
                    </div>
                    <div className="display-box">
                        <InputBox 
                            label={"LOQ (in ppm)"}
                            isRequired={true}
                            type={"text"}
                            ref={"loq"}
                            addInputStyle={`input-box-half`}
                            addClass={`input-box-container-half`}
                            updateValue={this.updateLOQValue.bind(this)}
                            //updateValue={(e) => {this.setState({loq: ReactDOM.findDOMNode(this.refs.analytical_method_ID.refs.input).value})}}
                        />
                    </div>
                </div>
            </div>
        )
    }
}
