import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import _ from 'lodash';
import './Card.scss';
import InputBox from '../InputBox/InputBox';
import SelectBox from '../selectBox/SelectBox';
import DivButton from '../DivButton/DivButton';
import MiniCards from './MiniCards';
import {default as UUID} from "node-uuid";

export default class Card extends Component {
    constructor(props){
        super(props);
        this.state = {
            swapSampling_API: {
                method_used: "",
                solvent_quantity: "",
                solvent_name: "",
                default_recovery: "",
                MOC: []
            },
            moc_display: false,
            divText: `Add MOC`,
            miniCard: [],
            miniCardsOptions: [
                {
                    name: "Stainless Steel",
                    _id: "1234567",
                    key: "STAINLESS_STEEL"
                },
                {
                    name: "Glass",
                    _id: "894RBNTNVN",
                    key: "GLASS"
                },
                {
                    name: "Teflon",
                    _id: "1234567UUUUU",
                    key: "TEFLON"
                },
                {
                    name: "Plastic",
                    _id: "874t68743y9g",
                    key: "PLASTIC"
                }
            ]
        }
    }
    // callback(){

    // }

    targetResidueType(){

    }
    deleteMiniCards(e){
        let prevList = this.state.miniCard;
        console.log("_id", e);

        _.forEach(prevList, (_element, _index) => {
            if(_element && _element.key===e){
                prevList.splice(_index, 1)
            }
        });
        this.setState({miniCard: prevList});
    }

    populateMiniCards(){
        let prevList = this.state.miniCard;
        this.id = UUID.v4();
        let nodeElement = {
            key: this.id,
            value: (<MiniCards key={this.state.miniCard.length} _id={this.id} deleteMiniCards={this.deleteMiniCards.bind(this)} callback={this.props.callback.bind(this)} />)
        };
        prevList.push(nodeElement);
        this.setState({miniCard: prevList})
    }
    removeMiniCards(){

    }
    displayMiniCards(){
        this.setState({ divText: `Add Another MOC`}, () => {
            this.populateMiniCards();
        });
    }
    render() {

        return (
            <div className="card-container">
                <div className="card-content">
                    {
                        (!this.props.bioburdenComponents
                            ?
                        <InputBox 
                            label={"Method Used"}
                            isRequired={true}
                            type={"text"}
                            ref={"method_used"}
                            updateValue={(e) =>{
                                this.setState({
                                    swapSampling_API: {
                                        method_used: ReactDOM.findDOMNode(this.refs.method_used.refs.input).value
                                    }
                                },() => {
                                    this.props.callback(this.state);
                                })
                            }}
                        />  
                        :
                        null  
                        )
                    }

                    {
                        (this.props && this.props.isRequired && !this.props.bioburdenComponents
                            ?
                            <div style={{display: "flex"}}>

                                {
                                    _.map(this.props.isRequired, (el,i) => {
                                        if(el==="SOLVENT_NAME"){
                                            return (
                                            <div key={`solvent`+i}>
                                                <InputBox 
                                                    label={"Solvent name"}
                                                    isRequired={true}
                                                    type={"text"}
                                                    ref={"solvent_name"}
                                                    addInputStyle={`input-box-half`}
                                                    addClass={`input-box-container-half`}
                                                    updateValue={(e) =>{
                                                        this.setState({
                                                            swapSampling_API:{
                                                                solvent_name: ReactDOM.findDOMNode(this.refs.solvent_name.refs.input).value
                                                            } 
                                                        },() => {
                                                            this.props.callback(this.state);
                                                        })
                                                    }}
                                                />
                                            </div>

                                            )
                                        }
                                        if(el==="SOLVENT_QUANTITY"){
                                            return (
                                                <div key={`solvent_qua`+i}>
                                                    <InputBox 
                                                        label={"Solvent Quantity"}
                                                        isRequired={true}
                                                        type={"text"}
                                                        ref={"solvent_quantity"}
                                                        addInputStyle={`input-box-half`}
                                                        addClass={`input-box-container-half`}
                                                        updateValue={(e) =>{
                                                            this.setState({
                                                                swapSampling_API:{
                                                                    solvent_quantity: ReactDOM.findDOMNode(this.refs.solvent_quantity.refs.input).value
                                                                }
                                                            },() => {
                                                                this.props.callback(this.state);
                                                            })
                                                        }}
                                                    />
                                                </div>

                                            )
                                        }
                                    })
                                }
                            </div>
                            :
                            null
                        )
                    }

                    {
                        (this.props.bioburdenComponents
                            ?

                            _.map([0,1], (el, i) => (
                                <RadioButton 
                                    key={i+"leuc"}
                                    label={(i==0 ? "Yes" : "No")}
                                    isRequired={false}
                                    type={"radio"}
                                    ref={(i==0) ? "recv_y" : "recv_n"}
                                    name={"lntc"}
                                    addInputStyle={`input-box-half`}
                                    addClass={`input-box-container-half`}
                                    updateValue={this.getRadioButtonResponse.bind(this)}
                                />
                            ))

                            :
                            null
                        )
                    }
                    <InputBox 
                        label={"Default Recovery"}
                        isRequired={true}
                        type={"text"}
                        ref={"default_recovery"}
                        updateValue={(e) =>{
                            this.setState({
                                swapSampling_API:{
                                    default_recovery: ReactDOM.findDOMNode(this.refs.default_recovery.refs.input).value
                                } 
                            },() => {
                                this.props.callback(this.state);
                            })
                        }}
                    />
                    <div>
                        <div style={{backgroundColor: '#EFEFEF', padding: '1.5em 0.5em 1.5em 0.5em'}}>
                            {_.map(this.state.miniCard,  (_elem, _ind) => (_elem.value))}

                        <DivButton 
                            ref={"add_moc"}
                            text={this.state.divText}
                            addClass={'miniCardButton'}
                            updateValue={(e) =>{
                                this.setState({
                                    moc_display: !this.state.moc_display,

                                },() => {
                                    this.displayMiniCards();
                                })
                            }}
                        />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
