import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import _ from 'lodash';
import './Card.scss';
import InputBox from '../InputBox/InputBox';
import SelectBox from '../selectBox/SelectBox';
import DivButton from '../DivButton/DivButton';

export default class Card extends Component {
    constructor(props){
        super(props);
        this.state = {
            swapSampling_API: {
                method_used: "",
                solvent_quantity: "",
                solvent_name: "",
                default_recovery: "",
                MOC: []
            },
            moc_display: false,
            divText: `Add MOC`,
            miniCard: null,
            miniCardsOptions: [
                {
                    name: "Stainless Steel",
                    _id: "1234567",
                    key: "STAINLESS_STEEL"
                },
                {
                    name: "Glass",
                    _id: "894RBNTNVN",
                    key: "GLASS"
                },
                {
                    name: "Teflon",
                    _id: "1234567UUUUU",
                    key: "TEFLON"
                },
                {
                    name: "Plastic",
                    _id: "874t68743y9g",
                    key: "PLASTIC"
                }
            ]
        }
    }
    // callback(){

    // }
    targetResidueType(){

    }
    populateMiniCards(){
        let miniCards = (
            <div>

                {/* <SelectBox 
                    label={`Select MOC`}
                    isRequired={true}
                    type={"select"}
                    ref={"select_moc"}
                    addInputStyle={`input-box-half`}
                    addClass={`input-box-container-half`}
                    options={this.state.miniCardsOptions}
                    updateValue={this.targetResidueType.bind(this)}
                /> */}
                <InputBox 
                    label={`Recovery (%)`}
                    isRequired={true}
                    type={"text"}
                    ref={"recovery_percent"}
                    addInputStyle={`input-box-half`}
                    addClass={`input-box-container-half`}
                    updateValue={(e) =>{
                        this.setState({
                            MOC: ReactDOM.findDOMNode(this.refs.recovery_percent.refs.input).value
                        },() => {
                            this.props.callback(this.state);
                        })
                    }}
                />
            </div>
        );
        // let cards = this.state.miniCard;
        // cards.push(miniCards);
        this.setState({miniCard: miniCards})
    }
    removeMiniCards(){

    }
    displayMiniCards(){
        if(this.state.moc_display){
            this.setState({ divText: `Remove MOC`}, () => {
                this.populateMiniCards();
            })
        }
        else{
            this.setState({ divText: `Remove MOC`}, () => {
                this.removeMiniCards();
            })
        }
    }
    render() {
        return (
            <div className="card-container">
                <div className="card-content">
                    <InputBox 
                        label={"Method Used"}
                        isRequired={true}
                        type={"text"}
                        ref={"method_used"}
                        updateValue={(e) =>{
                            this.setState({
                                swapSampling_API: {
                                    method_used: ReactDOM.findDOMNode(this.refs.method_used.refs.input).value
                                }
                            },() => {
                                this.props.callback(this.state);
                            })
                        }}
                    />
                    <div>
                        <div>
                            <InputBox 
                                label={"Solvent name"}
                                isRequired={true}
                                type={"text"}
                                ref={"solvent_name"}
                                addInputStyle={`input-box-half`}
                                addClass={`input-box-container-half`}
                                updateValue={(e) =>{
                                    this.setState({
                                        swapSampling_API:{
                                            solvent_name: ReactDOM.findDOMNode(this.refs.solvent_name.refs.input).value
                                        } 
                                    },() => {
                                        this.props.callback(this.state);
                                    })
                                }}
                            />
                        </div>
                        <div>
                            <InputBox 
                                label={"Solvent Quantity"}
                                isRequired={true}
                                type={"text"}
                                ref={"solvent_quantity"}
                                addInputStyle={`input-box-half`}
                                addClass={`input-box-container-half`}
                                updateValue={(e) =>{
                                    this.setState({
                                        swapSampling_API:{
                                            solvent_quantity: ReactDOM.findDOMNode(this.refs.solvent_quantity.refs.input).value
                                        }
                                    },() => {
                                        this.props.callback(this.state);
                                    })
                                }}
                            />
                        </div>
                    </div>

                    <InputBox 
                        label={"Default Recovery"}
                        isRequired={true}
                        type={"text"}
                        ref={"default_recovery"}
                        updateValue={(e) =>{
                            this.setState({
                                swapSampling_API:{
                                    default_recovery: ReactDOM.findDOMNode(this.refs.default_recovery.refs.input).value
                                } 
                            },() => {
                                this.props.callback(this.state);
                            })
                        }}
                    />
                    <div>
                        <DivButton 
                            ref={"add_moc"}
                            text={this.state.divText}
                            updateValue={(e) =>{
                                this.setState({
                                    moc_display: !this.state.moc_display,

                                },() => {
                                    this.displayMiniCards();
                                })
                            }}
                        />
                        {/* {
                            _.map(this.state.miniCard, (el, i) => (el))
                        } */}
                        {this.state.miniCard}
                    </div>
                </div>
            </div>
        )
    }
}
