import React from 'react';
import ReactDOM from 'react-dom';
import Button from '../contents/Button/Button';
import InputBox from '../contents/InputBox/InputBox';

class FormContainer extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }
    updateValue(e){
        //console.log("this.refs", this.refs.input.value)
        this.setState({
            value: ReactDOM.findDOMNode(this.refs.analytical_method_ID.refs.input).value
        });
        console.log(["data"], ReactDOM.findDOMNode(this.refs.analytical_method_ID.refs.input).value)
    }
    render(){
        return(
            <div className="form-container">
                <div className="box-container">
                    <InputBox 
                        label={"Analytical Method ID"}
                        isRequired={true}
                        type={"text"}
                        ref={"analytical_method_ID"}
                        reference={"analytics_medhod_id"}
                        updateValue={this.updateValue.bind(this)}
                    />
                </div>

            </div>
        )
    }
}
export default FormContainer;
