import React, { Component } from 'react'
import ReactDOM from 'react-dom';
import InputBox from '../contents/InputBox/InputBox';
import DivButton from '../contents/DivButton/DivButton';

export default class CardContents extends Component {
    constructor(props){
        super(props);
        this.state = {
            target_residue_type_list: [],
            lod: "",
            loq: "",
            swabDisplay: false,
            divText: `Configure swab sampling parameter`,
            displayDetails: null

        }
    }
    displayCards = (e) => {
        if(swabDisplay){
            this.setState({divText: `Remove swab sampling parameters`}, () => {
                this.callback();
            })
        }
        else{

        }
    }
    render() {
        return (
            <div className="card-contents">
                <div className="card-content-items">
                    <div className="display-box">
                        <InputBox 
                            label={"LOD (in ppm)"}
                            isRequired={true}
                            type={"text"}
                            ref={"lod"}
                            addInputStyle={`input-box-half`}
                            addClass={`input-box-container-half`}
                            updateValue={(e) =>{
                                this.setState({
                                    lod: ReactDOM.findDOMNode(this.refs.lod.refs.input).value
                                },() => {
                                    this.props.setLQD_LODValues(this.state);
                                })
                            }}
                        />
                    </div>
                    <div className="display-box">
                        <InputBox 
                            label={"LOQ (in ppm)"}
                            isRequired={true}
                            type={"text"}
                            ref={"loq"}
                            addInputStyle={`input-box-half`}
                            addClass={`input-box-container-half`}
                            updateValue={(e) => {
                                this.setState({
                                    loq: ReactDOM.findDOMNode(this.refs.loq.refs.input).value
                                }, () => {
                                    this.props.setLQD_LODValues(this.state);
                                });
                            }}
                        />
                    </div>

                </div>
                <div className="card-content-items">
                    <DivButton 
                        ref={"swab_sampling"}
                        text={this.state.divText}
                        updateValue={(e) =>{
                            this.setState({
                                swabDisplay: !this.state.swabDisplay,

                            },() => {
                                this.displayCards();
                            })
                        }}
                    />

                </div>
            </div>
        )
    }
}
