import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import _ from 'lodash';
import './Card.scss';
import InputBox from '../InputBox/InputBox';
import SelectBox from '../selectBox/SelectBox';
import DivButton from '../DivButton/DivButton';
import MiniCards from './MiniCards';
import {default as UUID} from "node-uuid";

export default class Card extends Component {
    constructor(props){
        super(props);
        this.state = {
            swapSampling_API: {
                method_used: "",
                solvent_quantity: "",
                solvent_name: "",
                default_recovery: "",
                MOC: []
            },
            moc_display: false,
            divText: `Add MOC`,
            miniCard: [],
            miniCardsOptions: [
                {
                    name: "Stainless Steel",
                    _id: "1234567",
                    key: "STAINLESS_STEEL"
                },
                {
                    name: "Glass",
                    _id: "894RBNTNVN",
                    key: "GLASS"
                },
                {
                    name: "Teflon",
                    _id: "1234567UUUUU",
                    key: "TEFLON"
                },
                {
                    name: "Plastic",
                    _id: "874t68743y9g",
                    key: "PLASTIC"
                }
            ]
        }
    }
    // callback(){

    // }
    targetResidueType(){

    }
    deleteMiniCards(e){
        let prevList = this.state.miniCard;
        console.log("_id", e);
        _.forEach(this.state.miniCard, (el, i) => {
            console.log("1.", ReactDOM.findDOMNode())
        })
        // if(prevList.length===1 && e===prevList.length)
        // {
        //     prevList.splice(e-1, 1);
        //     this.setState({miniCard: prevList});
        //     return;
        // }
        // prevList.splice(e, 1);
        // this.setState({miniCard: prevList})
    }

    populateMiniCards(){
        let prevList = this.state.miniCard;
        this.id = UUID.v4();
        let nodeElement = (
            <MiniCards key={this.state.miniCard.length} _id={this.id} ref={this.id} deleteMiniCards={this.deleteMiniCards.bind(this)} callback={this.props.callback.bind(this)} />
        );
        prevList.push(nodeElement);
        this.setState({miniCard: prevList})
    }
    removeMiniCards(){

    }
    displayMiniCards(){
        this.setState({ divText: `Add Another MOC`}, () => {
            this.populateMiniCards();
        });
    }
    render() {

        return (
            <div className="card-container">
                <div className="card-content">
                    <InputBox 
                        label={"Method Used"}
                        isRequired={true}
                        type={"text"}
                        ref={"method_used"}
                        updateValue={(e) =>{
                            this.setState({
                                swapSampling_API: {
                                    method_used: ReactDOM.findDOMNode(this.refs.method_used.refs.input).value
                                }
                            },() => {
                                this.props.callback(this.state);
                            })
                        }}
                    />
                    <div style={{display: "flex"}}>
                        <div>
                            <InputBox 
                                label={"Solvent name"}
                                isRequired={true}
                                type={"text"}
                                ref={"solvent_name"}
                                addInputStyle={`input-box-half`}
                                addClass={`input-box-container-half`}
                                updateValue={(e) =>{
                                    this.setState({
                                        swapSampling_API:{
                                            solvent_name: ReactDOM.findDOMNode(this.refs.solvent_name.refs.input).value
                                        } 
                                    },() => {
                                        this.props.callback(this.state);
                                    })
                                }}
                            />
                        </div>
                        <div>
                            <InputBox 
                                label={"Solvent Quantity"}
                                isRequired={true}
                                type={"text"}
                                ref={"solvent_quantity"}
                                addInputStyle={`input-box-half`}
                                addClass={`input-box-container-half`}
                                updateValue={(e) =>{
                                    this.setState({
                                        swapSampling_API:{
                                            solvent_quantity: ReactDOM.findDOMNode(this.refs.solvent_quantity.refs.input).value
                                        }
                                    },() => {
                                        this.props.callback(this.state);
                                    })
                                }}
                            />
                        </div>
                    </div>

                    <InputBox 
                        label={"Default Recovery"}
                        isRequired={true}
                        type={"text"}
                        ref={"default_recovery"}
                        updateValue={(e) =>{
                            this.setState({
                                swapSampling_API:{
                                    default_recovery: ReactDOM.findDOMNode(this.refs.default_recovery.refs.input).value
                                } 
                            },() => {
                                this.props.callback(this.state);
                            })
                        }}
                    />
                    <div>
                        <div style={{backgroundColor: '#EFEFEF', padding: '1.5em 0.5em 1.5em 0.5em'}}>
                            {_.map(this.state.miniCard,  (_elem, _ind) => (_elem))}

                        <DivButton 
                            ref={"add_moc"}
                            text={this.state.divText}
                            addClass={'miniCardButton'}
                            updateValue={(e) =>{
                                this.setState({
                                    moc_display: !this.state.moc_display,

                                },() => {
                                    this.displayMiniCards();
                                })
                            }}
                        />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
