import React from  'react';
import styles from './InputBox.scss';
import '../../App.scss';

class InputBox extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            name: (this.props && this.props.label ? this.props.label : "name"),
            isRequired: (this.props && this.props.isRequired ? this.props.isRequired: false)
        }
    }
    render(){
        return(
            <div className="input-box-container">
                <label><span className="mandatory-label">{this.props.isRequired ? `*` : null}</span>{this.state.name}</label>
                <input 
                    type="text" 
                    className="input-box" 
                />
            </div>
        )
    }
}
export default InputBox;
